SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_nivel_paralelismo (nm_tabela_p text) RETURNS bigint AS $body$
DECLARE

 
qt_nivel_paralelismo_w	smallint;
qt_bytes_cad_w		bigint;
qt_bytes_w		bigint;
qt_retorno_w		smallint := 0;
qt_reg_w		smallint := 0;

C01 CURSOR FOR 
	SELECT	qt_bytes, 
		qt_nivel_paralelismo 
	from	regra_tab_indice_pendente	 
	order by qt_bytes;


BEGIN 
 
begin 
	select	count(1) 
	into STRICT	qt_reg_w	 
	from	regra_tab_indice_pendente;	
	 
	if (qt_reg_w > 0) then 
		select	bytes 
		into STRICT	qt_bytes_w 
		from	user_segments 
		where	segment_name = nm_tabela_p;
		 
		open C01;
		loop 
		fetch C01 into	 
			qt_bytes_cad_w, 
			qt_nivel_paralelismo_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin 
			if (qt_bytes_w > qt_bytes_cad_w) then 
				qt_retorno_w := qt_nivel_paralelismo_w;
			end	if;
			end;
		end loop;
		close C01;
	end	if;
exception 
	when others then 
		qt_retorno_w	:= 0;
end;
 
return	qt_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_nivel_paralelismo (nm_tabela_p text) FROM PUBLIC;

