SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_nivel_prot (nr_interno_conta_p bigint) RETURNS bigint AS $body$
DECLARE

 
ie_status_w			integer;
ie_status_conta_w		smallint;
nr_seq_protocolo_w	bigint;


BEGIN 
 
select	ie_status_acerto, 
	nr_seq_protocolo 
into STRICT	ie_status_conta_w, 
	nr_seq_protocolo_w 
from 	conta_paciente 
where	nr_interno_conta	= nr_interno_conta_p;
 
if (coalesce(ie_status_conta_w,0) = 0) then 
	select nr_seq_protocolo, 
		ie_status_acerto 
	into STRICT 	nr_seq_protocolo_w, 
		ie_status_conta_w 
	from	conta_paciente a 
	where a.nr_interno_conta = nr_interno_conta_p;
end if;
 
if (ie_status_conta_w = 2) then 
	if (coalesce(nr_seq_protocolo_w::text, '') = '') then 
		ie_status_w := '6';
	else 
		select CASE WHEN ie_status_protocolo=1 THEN '8'  ELSE '10' END  
		into STRICT ie_status_w 
		from protocolo_convenio 
		where nr_seq_protocolo = nr_seq_protocolo_w;
	end if;
else 
	select CASE WHEN coalesce(b.dt_alta::text, '') = '' THEN '2'  ELSE '4' END  
	into STRICT ie_status_w 
	from	atendimento_paciente b, 
		conta_paciente a 
	where a.nr_atendimento = b.nr_atendimento 
	 and a.nr_interno_conta = nr_interno_conta_p;
end if;
 
return somente_numero(ie_status_w);
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
