CREATE OR REPLACE FUNCTION obter_nm_arquivo_synapse ( nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


nm_arquivo_w		varchar(255);
cd_identificador_w	varchar(5);



BEGIN

select  coalesce(max(cd_identificador),'')
into STRICT	cd_identificador_w
from	empresa_integr_dados
where	nr_seq_empresa_integr in (31,41);

select  distinct
	max(cd_identificador_w || a.nr_prescricao|| '_' ||to_char(b.dt_prescricao,'yyyymmdd') || '_' ||
	to_char(b.dt_prescricao,'hhmiss') ||  '.xml')
into STRICT	nm_arquivo_w
from 	prescr_procedimento a,
	prescr_medica b
where 	a.nr_prescricao = b.nr_prescricao
and	a.nr_prescricao = nr_prescricao_p;

return	nm_arquivo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_nm_arquivo_synapse ( nr_prescricao_p bigint) FROM PUBLIC;

