CREATE OR REPLACE FUNCTION obter_nm_arq_ou_dir (ds_arquivo_p text) RETURNS varchar AS $body$
DECLARE


ie_dir_w varchar(1);
ds_arquivo_w varchar(255);

BEGIN

ie_dir_w := obter_param_usuario(3004, 128, obter_perfil_ativo, obter_usuario_ativo, obter_estabelecimento_ativo, ie_dir_w);

if (ie_dir_w = 'S' or coalesce(ie_dir_w::text, '') = '')then
    ds_arquivo_w := ds_arquivo_p;
else
    select substr(obter_nome_arquivo(ds_arquivo_p,'N'),1,255)
    into STRICT ds_arquivo_w
;
end if;

return	ds_arquivo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
