CREATE OR REPLACE FUNCTION obter_nm_dieta_cardapio (nr_seq_cardapio_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w varchar(255);

BEGIN
if (coalesce(nr_seq_cardapio_p, 0) > 0) then
	begin
	select	max(substr(obter_nome_dieta(a.cd_dieta),1,255))
	into STRICT	ds_retorno_w
	from    nut_cardapio_dia a,
		nut_cardapio b
	where	b.nr_sequencia = nr_seq_cardapio_p
	and	a.nr_sequencia = b.nr_seq_card_dia;

	end;
end if;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
