CREATE OR REPLACE FUNCTION obter_nm_documento_opme (nr_seq_documento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w    varchar(255);


BEGIN

if (nr_seq_documento_p IS NOT NULL AND nr_seq_documento_p::text <> '') then

	SELECT 	MAX(nm_documento)
	into STRICT	ds_retorno_w
	FROM 	documento_opme
	WHERE   IE_SITUACAO = 'A'
	AND	nr_sequencia = nr_seq_documento_p;

end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
