CREATE OR REPLACE FUNCTION obter_nm_elemento_ehr (NR_SEQUENCIA_P text) RETURNS varchar AS $body$
DECLARE

	
    CD_EXP_ELEMENTO_W bigint;
    NM_ELEMENTO_W varchar(80);
	

BEGIN
   
    SELECT	MAX(CD_EXP_ELEMENTO), MAX(NM_ELEMENTO)
	INTO STRICT 	CD_EXP_ELEMENTO_W, NM_ELEMENTO_W
	FROM 	EHR_ELEMENTO
	WHERE 	NR_SEQUENCIA = NR_SEQUENCIA_P;
	
	RETURN coalesce( coalesce(substr(obter_desc_expressao(CD_EXP_ELEMENTO_W), 1, 255), substr(NM_ELEMENTO_W, 1, 255)), '');

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
