CREATE OR REPLACE FUNCTION obter_nm_empresa (cd_cgc_p text) RETURNS varchar AS $body$
DECLARE


nm_empresa_w	varchar(255);


BEGIN
if (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') then
	begin
	select	max(ds_razao_social)
	into STRICT	nm_empresa_w
	from	pessoa_juridica
	where	cd_cgc = cd_cgc_p;
	end;
end if;
return nm_empresa_w;
end;
$body$
