CREATE OR REPLACE FUNCTION obter_nm_estip_contrato_web ( nr_seq_segurado_p bigint) RETURNS varchar AS $body$
DECLARE

 
nr_seq_congenere_w			bigint;
nr_seq_intercambio_w		bigint;
nr_seq_contrato_w			bigint;			
nm_estipulante_w				varchar(255);


BEGIN 
 
begin 
	select	nr_seq_congenere, 
			nr_seq_intercambio, 
			nr_seq_contrato 
	into STRICT		nr_seq_congenere_w, 
			nr_seq_intercambio_w, 
			nr_seq_contrato_w 
	from	pls_segurado 
	where	nr_sequencia = nr_seq_segurado_p;
exception 
when others then 
	nr_seq_congenere_w := null;
	nr_seq_intercambio_w := null;
	nr_seq_contrato_w := null;
end;
 
if (nr_seq_contrato_w IS NOT NULL AND nr_seq_contrato_w::text <> '') then 
	nm_estipulante_w := pls_obter_dados_contrato(nr_seq_contrato_w,'E');
elsif (nr_seq_intercambio_w IS NOT NULL AND nr_seq_intercambio_w::text <> '') then 
	nm_estipulante_w := pls_obter_dados_contrato_inter(nr_seq_intercambio_w,null,'E');
elsif (nr_seq_congenere_w IS NOT NULL AND nr_seq_congenere_w::text <> '') then 
	nm_estipulante_w := pls_obter_nome_congenere(nr_seq_congenere_w);
end if;
 
return	nm_estipulante_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
