CREATE OR REPLACE FUNCTION obter_nm_hemocomponente_adep ( nr_prescricao_p bigint, nr_seq_procedimento_p bigint ) RETURNS varchar AS $body$
DECLARE


nr_seq_derivado_w	prescr_procedimento.nr_seq_derivado%type;
nr_seq_exame_sangue_w	prescr_procedimento.nr_seq_exame_sangue%type;
cd_procedimento_w	prescr_procedimento.cd_procedimento%type;
ie_origem_proced_w	prescr_procedimento.ie_origem_proced%type;
nr_seq_proc_interno_w	prescr_procedimento.nr_seq_proc_interno%type;
ds_retorno_w		varchar(50)	:= '';


BEGIN

	select 	max(nr_seq_derivado),
		max(nr_seq_exame_sangue),
		max(cd_procedimento),
		max(ie_origem_proced),
		max(nr_seq_proc_interno)
	into STRICT	nr_seq_derivado_w,
		nr_seq_exame_sangue_w,
		cd_procedimento_w,
		ie_origem_proced_w,
		nr_seq_proc_interno_w
	from	prescr_procedimento
	where	nr_prescricao = nr_prescricao_p
	and	nr_sequencia = nr_seq_procedimento_p;

	if (nr_seq_derivado_w IS NOT NULL AND nr_seq_derivado_w::text <> '') then

		ds_retorno_w	:= substr(Obter_desc_san_derivado(nr_seq_derivado_w),1,50);

	elsif (nr_seq_exame_sangue_w IS NOT NULL AND nr_seq_exame_sangue_w::text <> '') then

		ds_retorno_w	:=  substr(Obter_desc_san_exame(nr_seq_exame_sangue_w),1,50);

	elsif (cd_procedimento_w IS NOT NULL AND cd_procedimento_w::text <> '') then

		ds_retorno_w	:= substr(Obter_Desc_Prescr_Proc(cd_procedimento_w, ie_origem_proced_w, nr_seq_proc_interno_w),1,50);

	end if;

	return 	ds_retorno_w;


end;
$body$
