CREATE OR REPLACE FUNCTION obter_nm_medico_crm_externo ( nr_crm_p text) RETURNS varchar AS $body$
DECLARE

 
nm_pessoa_fisica_w	varchar(255);		

BEGIN 
if (nr_crm_p IS NOT NULL AND nr_crm_p::text <> '') then 
	begin 
	select	max(substr(obter_nome_pf(cd_pessoa_fisica),1,150)) 
	into STRICT	nm_pessoa_fisica_w 
	from	medico 
	where	nr_crm = nr_crm_p;
	end;											
end if;
return	nm_pessoa_fisica_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
