CREATE OR REPLACE FUNCTION obter_nm_mutirao_agenda_int (nr_seq_agenda_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_mutirao_w	bigint;
nm_mutirao_w		varchar(255);


BEGIN

if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '')then

	select  max(b.nr_seq_multirao)
	into STRICT 	nr_seq_mutirao_w
	from    agenda_integrada a,
			agenda_integrada_item b
	where   a.nr_sequencia = b.nr_seq_agenda_int
	and     a.nr_sequencia = nr_seq_agenda_p;

	if (nr_seq_mutirao_w IS NOT NULL AND nr_seq_mutirao_w::text <> '')then

		select	substr(max(b.nm_mutirao),1,255)
		into STRICT	nm_mutirao_w
		from	mutirao_lista_espera b
		where 	b.nr_sequencia = nr_seq_mutirao_w;

	end if;

end if;

return	nm_mutirao_w;

end;
