CREATE OR REPLACE FUNCTION obter_nm_mutirao_lista_espera (nr_seq_lista_epera_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno	varchar(4000);				

BEGIN

if (nr_seq_lista_epera_p IS NOT NULL AND nr_seq_lista_epera_p::text <> '')then
	
	select  max(b.nm_mutirao)
	into STRICT 	ds_retorno
	from    paciente_mutirao a,
			mutirao_lista_espera b
	where   a.nr_seq_mutirao = b.nr_sequencia
	and     a.nr_seq_lista_espera = nr_seq_lista_epera_p;
	
end if;


return	coalesce(ds_retorno, '');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
