SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_nm_protocolo_npt_adul ( nr_prescricao_p bigint, nr_seq_item_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

select 	substr(obter_desc_prot_npt(max(nr_seq_protocolo)),1,255)
into STRICT	ds_retorno_w
from	nut_pac
where 	nr_sequencia in (	SELECT	b.nr_seq_nut_pac
								from	prescr_material b
								where	nr_prescricao = nr_prescricao_p
								and		nr_sequencia  = nr_seq_item_p);

if (coalesce(ds_retorno_w::text, '') = '') then

	select	substr(obter_desc_material(max(cd_material)),1,255)
	into STRICT	ds_retorno_w
	from	prescr_material
	where	nr_prescricao = nr_prescricao_p
	and		nr_sequencia  = nr_seq_item_p;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
