CREATE OR REPLACE FUNCTION obter_nm_sistema_intpd ( nr_seq_evento_sistema_p bigint, ie_opcao bigint) RETURNS varchar AS $body$
DECLARE


ds_valor varchar(255) := '';


BEGIN
if (coalesce(nr_seq_evento_sistema_p,0) > 0) then
	if (ie_opcao = 0) then
		select	coalesce(substr(max(s.ds_sistema),1,255),'')
		into STRICT	ds_valor
		from	intpd_sistemas s,
			intpd_eventos_sistema es
		where	s.nr_sequencia = es.nr_seq_sistema
		and		es.nr_sequencia = nr_seq_evento_sistema_p;
	elsif (ie_opcao = 1) then
		select	coalesce(substr(max(s.nr_sequencia),1,255),'')
		into STRICT	ds_valor
		from	intpd_sistemas s,
			intpd_eventos_sistema es
		where	s.nr_sequencia = es.nr_seq_sistema
		and		es.nr_sequencia = nr_seq_evento_sistema_p;
	end if;
end if;

return ds_valor;

end	;
