nm_usuario_w varchar(15);


BEGIN


if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (ie_status_pa_p IS NOT NULL AND ie_status_pa_p::text <> '') then

	select 	max(a.nm_usuario)
