SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_nomes_pacotes (nr_interno_conta_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


nm_pacotes_w	varchar(4000);
ds_pacote_w	varchar(255);
qt_conta_w	bigint;

c01 CURSOR FOR
	SELECT  lower(substr(obter_descricao_procedimento(cd_procedimento, ie_origem_procred),1,200)) ds_pacotes
	from    w_gestao_pacote
	where   nr_interno_conta = nr_interno_conta_p
	and 	nm_usuario	 = nm_usuario_p
	group by substr(obter_descricao_procedimento(cd_procedimento, ie_origem_procred),1,200);


BEGIN

nm_pacotes_w:= '';
qt_conta_w  := 0;

open C01;
loop
fetch C01 into
	ds_pacote_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	qt_conta_w  := qt_conta_w + 1;
	nm_pacotes_w:= nm_pacotes_w || InitCap(substr(ds_pacote_w,1,30)) || '... + ';
	end;
end loop;
close C01;

if (qt_conta_w < 2) then
	nm_pacotes_w:= InitCap(ds_pacote_w);
else
	nm_pacotes_w:= substr(nm_pacotes_w, 1, length(nm_pacotes_w) - 6);
end if;

return	substr(nm_pacotes_w,1,255);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_nomes_pacotes (nr_interno_conta_p bigint, nm_usuario_p text) FROM PUBLIC;

