CREATE OR REPLACE FUNCTION obter_nome_agenda_cons_esp (cd_agenda_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(255);


BEGIN 
 
select	substr(coalesce(obter_nome_pf(a.cd_pessoa_fisica), a.ds_curta)||' - '||coalesce(obter_nome_especialidade(a.cd_especialidade),wheb_mensagem_pck.get_texto(796797)),1,255) 
into STRICT	ds_retorno_w 
from	agenda a 
where	a.cd_agenda	= cd_agenda_p;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
