CREATE OR REPLACE FUNCTION obter_nome_anestesista (P_NR_CIRURGIA bigint) RETURNS varchar AS $body$
DECLARE

  RET varchar(255) := NULL;


BEGIN

    IF (P_NR_CIRURGIA IS NOT NULL AND P_NR_CIRURGIA::text <> '') THEN
      SELECT CASE WHEN INSTR(T.NM, ' ', -1)=0 THEN                     T.NM  ELSE SUBSTR(T.NM, INSTR(T.NM, ' ', -1) + 1) || ', ' ||                    SUBSTR(T.NM, 1, INSTR(T.NM, ' ', -1) - 1) END
        INTO STRICT RET
        FROM (SELECT OBTER_ANESTESISTA_CIRURGIA(P_NR_CIRURGIA,'N') NM ) T;
    END IF;

    RETURN RET;

END;
$body$
