SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_nome_arquivo_ftp_oc ( nr_ordem_compra_p bigint, ie_formato_p text) RETURNS varchar AS $body$
DECLARE

 
nm_arquivo_w		varchar(255);
cd_cnpj_estab_w		varchar(14);			
			 

BEGIN 
 
if (ie_formato_p = '0') then 
	 
	select	obter_cgc_estabelecimento(cd_estabelecimento) 
	into STRICT	cd_cnpj_estab_w 
	from	ordem_compra 
	where	nr_ordem_compra = nr_ordem_compra_p;
	 
	nm_arquivo_w := substr(nr_ordem_compra_p || '_' || cd_cnpj_estab_w || '.xml',1,255);
else 
	nm_arquivo_w := '';
end if;
 
return	nm_arquivo_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
