CREATE OR REPLACE FUNCTION obter_nome_classif_incap ( cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);

BEGIN
ds_retorno_w := '';

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

		select 	substr(RP_OBTER_DESC_DEFICIENCIA(max(nr_seq_tipo_deficiencia)),1,255)
		into STRICT	ds_retorno_w
		from	rp_lista_espera_modelo
		where	cd_pessoa_fisica = cd_pessoa_fisica_p;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
