SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_nome_codigo_cr (cd_pessoa_fisica_p text, Ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


nm_pessoa_fisica_w	varchar(60);
cd_cr_w			varchar(20);
ie_sexo_w		varchar(1);
cd_medico_w		varchar(10);
nr_seq_conselho_w	bigint;
sg_conselho_w		varchar(60);
ds_retorno_w		varchar(100);


BEGIN

select	coalesce(max(a.nm_pessoa_fisica),''),
	coalesce(max(coalesce(b.nr_crm, a.ds_codigo_prof)),''),
	coalesce(max(a.ie_sexo),'M'),
	coalesce(max(b.cd_pessoa_fisica),null),
	coalesce(max(a.NR_SEQ_CONSELHO),0)
into STRICT	nm_pessoa_fisica_w,
	cd_cr_w,
	ie_sexo_w,
	cd_medico_w,
	nr_seq_conselho_w
FROM pessoa_fisica a
LEFT OUTER JOIN medico b ON (a.cd_pessoa_fisica = b.cd_pessoa_fisica)
WHERE a.cd_pessoa_fisica = cd_pessoa_fisica_p;

if (nm_pessoa_fisica_w IS NOT NULL AND nm_pessoa_fisica_w::text <> '') then
	if (ie_opcao_p = 'NC') then
		ds_retorno_w := nm_pessoa_fisica_w||' ';
	elsif (ie_sexo_w = 'M') then
		ds_retorno_w := '';
	else
		ds_retorno_w := '';
	end if;
	ds_retorno_w := ds_retorno_w||nm_pessoa_fisica_w||' ';
	if (cd_medico_w IS NOT NULL AND cd_medico_w::text <> '') and (ie_opcao_p <> 'NCI') then
		ds_retorno_w := ds_retorno_w||'CRM:  '||cd_cr_w;
	elsif (ie_opcao_p = 'NC') or (ie_opcao_p = 'NCI') then
		begin
		if (nr_seq_conselho_w > 0) then
			select	a.sg_conselho
			into STRICT 	sg_conselho_w
			from 	conselho_profissional a
			where	a.nr_sequencia = nr_seq_conselho_w;
		end if;
		ds_retorno_w := ds_retorno_w||sg_conselho_w||': '||cd_cr_w;
		end;
	else
		ds_retorno_w := ds_retorno_w||'CRF:  '||cd_cr_w;
	end if;
else
	ds_retorno_w := '';
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
