CREATE OR REPLACE FUNCTION obter_nome_contato ( CD_PESSOA_FISICA_P text, IE_TIPO_COMPLEMENTO_P bigint) RETURNS varchar AS $body$
DECLARE


/* TIPO COMPLEMENTO 	*/

/*	1 = Residencial 	*/

/* 	2 = Comercial 	*/

/*	3 = Responsável	*/

/*	4 = Pai		*/

/*	5 = Mãe		*/

/*	6 = Conjuge		*/

NM_CONTATO_W			varchar(60);


BEGIN

begin
select nm_contato
into STRICT	 nm_contato_w
from	 compl_pessoa_fisica
where	 cd_pessoa_fisica		= cd_pessoa_fisica_p
and	 ie_tipo_complemento	= ie_tipo_complemento_p;
exception
	 when others then
	 nm_contato_w := ' ';
end;

return nm_contato_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
