CREATE OR REPLACE FUNCTION obter_nome_cpf_cgc (nr_cpf_cgc_p text) RETURNS varchar AS $body$
DECLARE

			 
ds_retorno_w		varchar(255);
cd_pessoa_fisica_w	pessoa_fisica.cd_pessoa_fisica%type;
cd_cgc_w		pessoa_juridica.cd_cgc%type;


BEGIN 
 
if (length(nr_cpf_cgc_p) = 11) then 
 
	select	max(cd_pessoa_fisica) 
	into STRICT	cd_pessoa_fisica_w 
	from	pessoa_fisica 
	where	nr_cpf = nr_cpf_cgc_p;
						 
elsif (length(nr_cpf_cgc_p) = 14) then 
	 
	cd_cgc_w := nr_cpf_cgc_p;
	 
end if;
	 
ds_retorno_w :=	obter_nome_pf_pj(cd_pessoa_fisica_w,cd_cgc_w);
	 
return	ds_retorno_w;
 
end;
