CREATE OR REPLACE FUNCTION obter_nome_curto_depto_medico ( cd_departamento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(100);


BEGIN
if (cd_departamento_p IS NOT NULL AND cd_departamento_p::text <> '') then
	begin
	select	substr(DS_SHORT_NAME,1,100)
	into STRICT	ds_retorno_w
	from	DEPARTAMENTO_MEDICO
	where	cd_departamento = cd_departamento_p;

	end;
end if;

return ds_retorno_w;

end;
