CREATE OR REPLACE FUNCTION obter_nome_departamento_medico ( cd_departamento_p bigint, ie_short_name_p text default 'N') RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(100);


BEGIN
if (cd_departamento_p IS NOT NULL AND cd_departamento_p::text <> '') then
	begin
		select	CASE WHEN ie_short_name_p='S' THEN  COALESCE(DS_SHORT_NAME, substr(DS_DEPARTAMENTO, 1, 6))  ELSE substr(DS_DEPARTAMENTO,1,100) END
		into STRICT	ds_retorno_w
		from	DEPARTAMENTO_MEDICO
		where	cd_departamento = cd_departamento_p;
	exception
	when no_data_found then
		ds_retorno_w := null;
	end;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
