CREATE OR REPLACE FUNCTION obter_nome_depart_por_atend (NR_ATENDIMENTO_P bigint) RETURNS varchar AS $body$
DECLARE

  RETORNO_W DEPARTAMENTO_MEDICO.DS_DEPARTAMENTO%TYPE;

BEGIN
    BEGIN
      SELECT OBTER_NOME_DEPART_POR_SETOR(CD_SETOR_USUARIO_ATEND)
      INTO STRICT   RETORNO_W
      FROM   ATENDIMENTO_PACIENTE
      WHERE  NR_ATENDIMENTO = NR_ATENDIMENTO_P;
    EXCEPTION
      WHEN no_data_found THEN
        RETORNO_W := ' ';
    END;
RETURN RETORNO_W;
END;
