CREATE OR REPLACE FUNCTION obter_nome_depart_por_setor (CD_SETOR_ATENDIMENTO bigint) RETURNS varchar AS $body$
DECLARE

  RETORNO_W DEPARTAMENTO_MEDICO.DS_DEPARTAMENTO%TYPE;

BEGIN
    BEGIN
      SELECT DS_DEPARTAMENTO
      INTO STRICT   RETORNO_W
      FROM   DEPARTAMENTO_MEDICO
      WHERE  CD_DEPARTAMENTO = OBTER_DEPARTAMENTO_SETOR(CD_SETOR_ATENDIMENTO);
    EXCEPTION
      WHEN no_data_found THEN
        RETORNO_W := ' ';
    END;
RETURN RETORNO_W;
END;
