CREATE OR REPLACE FUNCTION obter_nome_diretor ( ie_cargo_p text, cd_estabelecimento_p text, ie_opcao_p text default 'NCER') RETURNS varchar AS $body$
DECLARE

 
ds_medico_w	varchar(255);
			

BEGIN 
 
select	max(obter_nome_medico(cd_pessoa_fisica, coalesce(ie_opcao_p,'NCER'))) 
into STRICT	ds_medico_w 
from	ESTAB_DIRETORIA 
where	ie_cargo		= ie_cargo_p 
and	cd_estabelecimento	= cd_estabelecimento_p 
and	clock_timestamp()	between dt_inicial and coalesce(dt_fim_vigencia,clock_timestamp() + interval '1 days');
 
return	ds_medico_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
