CREATE OR REPLACE FUNCTION obter_nome_enf_evol (nr_atendimento_p bigint, ie_evolucao_clinica_p text) RETURNS varchar AS $body$
DECLARE


nm_enf_w	varchar(255);


BEGIN
select	min(substr(obter_nome_usuario(nm_usuario),1,255))
into STRICT	nm_enf_w
from	evolucao_paciente
where	nr_atendimento = nr_atendimento_p
and		ie_evolucao_clinica = ie_evolucao_clinica_p
and		(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
and		coalesce(dt_inativacao::text, '') = '';

return	nm_enf_w;

end;
