CREATE OR REPLACE FUNCTION obter_nome_estabelecimento_rel (CD_ESTABELECIMENTO_P bigint) RETURNS varchar AS $body$
DECLARE


ds_razao_social_w	pessoa_juridica.ds_razao_social%type;
nm_fantasia_w		varchar(80);
ie_razao_fantasia_w	varchar(2);
ds_retorno_w		pessoa_juridica.ds_razao_social%type;


BEGIN
if (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') then
	begin
	select	coalesce(a.ie_nome_relatorio,'R'),
		b.ds_razao_social,
		coalesce(a.nm_fantasia_estab,b.nm_fantasia)
	into STRICT	ie_razao_fantasia_w,
		ds_razao_social_w,
		nm_fantasia_w
	from	estabelecimento a,
             	pessoa_juridica b
	where	a.cd_cgc = b.cd_cgc
	and	a.cd_estabelecimento = cd_estabelecimento_p;
	end;
end if;

if (ie_razao_fantasia_w = 'R') then
	ds_retorno_w	:= ds_razao_social_w;
elsif (ie_razao_fantasia_w = 'F') then
	ds_retorno_w	:= nm_fantasia_w;
end if;

RETURN ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
