CREATE OR REPLACE FUNCTION obter_nome_estabelecimento ( CD_ESTABELECIMENTO_P bigint) RETURNS varchar AS $body$
DECLARE


ds_razao_social_w		pessoa_juridica.ds_razao_social%type;
nm_fantasia_w		pessoa_juridica.ds_razao_social%type;
ie_razao_fantasia_w	estabelecimento.ie_razao_fantasia%type;
ds_retorno_w		varchar(255);
nm_fantasia_estab_w	estabelecimento.nm_fantasia_estab%type;


BEGIN
if (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') then
	begin
	/* Francisco - OS 76261 - 18/12/2007 */

	select	coalesce(a.ie_razao_fantasia,'R'),
		b.ds_razao_social,
		b.nm_fantasia,
		a.nm_fantasia_estab
	into STRICT	ie_razao_fantasia_w,
		ds_razao_social_w,
		nm_fantasia_w,
		nm_fantasia_estab_w
	from	estabelecimento a,
             	pessoa_juridica b
	where	a.cd_cgc = b.cd_cgc
        and 	a.cd_estabelecimento = cd_estabelecimento_p;
	end;
end if;

if (ie_razao_fantasia_w = 'R') then
	ds_retorno_w	:= ds_razao_social_w;
elsif (ie_razao_fantasia_w = 'F') then
	ds_retorno_w	:= nm_fantasia_w;
elsif (ie_razao_fantasia_w = 'E') then
	ds_retorno_w	:= nm_fantasia_estab_w;
end if;

RETURN ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
