CREATE OR REPLACE FUNCTION obter_nome_funcionario ( cd_funcionario_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(100);


BEGIN 
 
if (cd_funcionario_p IS NOT NULL AND cd_funcionario_p::text <> '') then 
	select	max(SUBSTR(OBTER_NOME_PF(CD_PESSOA_FISICA),0,100)) 
	into STRICT	ds_retorno_w 
	from	pessoa_fisica 
	where	cd_funcionario	= cd_funcionario_p;
	 
end if;
	 
return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
