CREATE OR REPLACE FUNCTION obter_nome_grupo_producao ( cd_grupo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(80);


BEGIN
if (cd_grupo_p IS NOT NULL AND cd_grupo_p::text <> '') then
	begin
	select   ds_grupo
	into STRICT	 ds_retorno_w
	from	 nut_grupo_producao
