CREATE OR REPLACE FUNCTION obter_nome_grupo_servico (nr_prescricao_p bigint, ie_zona_cinza_p text) RETURNS varchar AS $body$
DECLARE

dt_inicio_prescr_w		timestamp;
dt_validade_prescr_w		timestamp;
nr_horas_validade_w		bigint;
ds_grupo_w			varchar(255);


BEGIN

select	max(nr_horas_validade),
	max(dt_inicio_prescr),
	max(dt_validade_prescr)
into STRICT	nr_horas_validade_w,
	dt_inicio_prescr_w,
	dt_validade_prescr_w
from	prescr_medica
where	nr_prescricao	= nr_prescricao_p;

if (ie_zona_cinza_p	= 'S') and (nr_horas_validade_w	> 24) then
	ds_grupo_w	:= to_char(dt_inicio_prescr_w,'dd/mm hh24:mi')||' - '||to_char((dt_validade_prescr_w - 1),'dd/mm hh24:mi');
elsif (ie_zona_cinza_p	= 'N') then
	if (nr_horas_validade_w	> 24) then
		ds_grupo_w	:= to_char(((dt_validade_prescr_w - 1) + 1/1440),'dd/mm hh24:mi')||' - '||to_char(dt_validade_prescr_w,'dd/mm hh24:mi');
	else
		ds_grupo_w	:= to_char(dt_inicio_prescr_w,'dd/mm hh24:mi')||' - '||to_char(dt_validade_prescr_w,'dd/mm hh24:mi');
	end if;
end if;

return	ds_grupo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
