SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_nome_itens_desdobrar ( nr_sequencia_p text, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


nr_sequencia_w	varchar(4000);
nr_seq_aux_w	varchar(14);
k		integer;
ds_retorno_w	varchar(4000);
ds_descricao_w	varchar(155);


BEGIN
ds_retorno_w 	:= '';
ds_descricao_w  := '';
nr_sequencia_w 	:= nr_sequencia_p;

while	(nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') loop
	begin
	select	position(',' in nr_sequencia_w)
	into STRICT	k
	;

	if (k > 1) and ((substr(nr_sequencia_w, 1, k -1) IS NOT NULL AND (substr(nr_sequencia_w, 1, k -1))::text <> '')) then
		nr_seq_aux_w			:= substr(nr_sequencia_w, 1, k-1);
		nr_seq_aux_w			:= replace(nr_seq_aux_w, ',','');
		nr_sequencia_w			:= substr(nr_sequencia_w, k + 1, 2000);
	elsif (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
		nr_seq_aux_w			:= replace(nr_sequencia_w,',','');
		nr_sequencia_w			:= '';
	end if;


	select 	substr(obter_desc_material(cd_material),1,100)
	into STRICT	ds_descricao_w
	from 	prescr_mat_hor
	where 	nr_sequencia = nr_seq_aux_w;

	ds_retorno_w := ds_retorno_w || ds_descricao_w || ',';

	end;
end loop;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
