CREATE OR REPLACE FUNCTION obter_nome_lib_gpt (nm_usuario_lib_p text ) RETURNS varchar AS $body$
DECLARE


/*function criada para obter nome dos usuários que ficaram registrados no campo com o CD_PESSOA_FISICA,
pois era assim que o sistema estava gravando até a alteração para gravar de fato o NM_USUARIO no campo correspondente*/
ds_retorno_w		varchar(60);


BEGIN
if (nm_usuario_lib_p IS NOT NULL AND nm_usuario_lib_p::text <> '') then
	begin
	
	ds_retorno_w	:= substr(obter_pf_usuario(nm_usuario_lib_p,'N'),1,60);
	
	if (coalesce(ds_retorno_w::text, '') = '') and (OBTER_SE_SOMENTE_NUMERO(nm_usuario_lib_p) = 'S') then
		ds_retorno_w	:= substr(obter_nome_pf(nm_usuario_lib_p),1,60);
	end if;
	
	end;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
