CREATE OR REPLACE FUNCTION obter_nome_mae (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ds_nome_mae_w        	varchar(255)  := '';				
ds_nome_contato_w	varchar(255)  := '';


BEGIN

-- verifica pelo cd_pessoa_mae
select SUBSTR(OBTER_NOME_PF(b.CD_PESSOA_FISICA),0,255) nm_mae
  into STRICT ds_nome_mae_w
  FROM pessoa_fisica a
LEFT OUTER JOIN pessoa_fisica b ON (a.cd_pessoa_mae = b.cd_pessoa_fisica)
WHERE a.cd_pessoa_fisica = cd_pessoa_fisica_p;

-- pega do complemento tipo mae

--pega do nome da mae
if (coalesce(ds_nome_mae_w::text, '') = '') then 

select a.nm_contato,
       SUBSTR(OBTER_NOME_PF(b.CD_PESSOA_FISICA),0,255) nm_mae
  into STRICT ds_nome_contato_w, 
       ds_nome_mae_w     
  FROM compl_pessoa_fisica a
LEFT OUTER JOIN pessoa_fisica b ON (a.cd_pessoa_fisica_ref = b.cd_pessoa_fisica)
WHERE a.ie_tipo_complemento = 5 and a.cd_pessoa_fisica = cd_pessoa_fisica_p;

   if (coalesce(ds_nome_mae_w::text, '') = '') then
	ds_nome_mae_w := ds_nome_contato_w;
   end if;

end if;

return	ds_nome_mae_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
