CREATE OR REPLACE FUNCTION obter_nome_medico_adic (nr_prescricao_p bigint, nr_seq_procedimento_p bigint ) RETURNS varchar AS $body$
DECLARE

nm_medico_w	varchar(60);
nm_medicos_solic_w varchar(2000);
C01 CURSOR FOR
	SELECT SUBSTR(OBTER_NOME_PF(a.CD_PESSOA_FISICA), 0, 60) 
	from 	pessoa_fisica a, 
		prescr_proced_prof_adic b 
	where b.cd_profissional = a.cd_pessoa_fisica 
	and b.ie_tipo_profissional = '1' 
	and b.nr_prescricao = nr_prescricao_p 
	and b.nr_seq_procedimento = nr_seq_procedimento_p;


BEGIN 
open C01;
loop 
fetch C01 into	 
	nm_medico_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	if ( coalesce(nm_medicos_solic_w::text, '') = '' ) then 
		nm_medicos_solic_w := nm_medico_w;
	else 
		nm_medicos_solic_w := nm_medicos_solic_w ||','|| nm_medico_w;
	end if;
	end;
end loop;
close C01;
 
 
return	nm_medicos_solic_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
