CREATE OR REPLACE FUNCTION obter_nome_medico_eup (cd_pessoa_fisica_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_nm_guerra_w varchar(1);		
ds_retorno_w 	varchar(255);			
			 

BEGIN 
 
	Select 	coalesce(max(ie_utiliza_nm_guerra), 'N') 
	into STRICT ie_nm_guerra_w 
	from parametro_atendimento 
	where cd_estabelecimento = cd_estabelecimento_p;
	 
	if (ie_nm_guerra_w = 'S') then 
	begin 
		Select coalesce(trim(both MAX(nm_guerra)), max(OBTER_NOME_PF(cd_pessoa_fisica_p))) 
		into STRICT ds_retorno_w 
		from medico 
		where cd_pessoa_fisica = cd_pessoa_fisica_p;
	exception 
	when others then 
		ds_retorno_w := '';
	end;
	else 		 
	begin 
		select max(OBTER_NOME_PF(cd_pessoa_fisica_p)) 
		into STRICT ds_retorno_w 
		;
	exception 
	when others then 
		ds_retorno_w := '';	
	end;
 
	end if;
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
