CREATE OR REPLACE FUNCTION obter_nome_medico_rxt_tumor (nr_seq_tratamento_p bigint, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE

ds_medico_w	varchar(80);


BEGIN

if (nr_seq_tratamento_p IS NOT NULL AND nr_seq_tratamento_p::text <> '') then

select 	max(a.nm_pessoa_fisica ||' (CRM ' ||c.nr_crm||')')
into STRICT	ds_medico_w
from 	pessoa_fisica a,
	rxt_tumor b,
	medico c,
	rxt_tratamento d,
	rxt_agenda e
where	c.cd_pessoa_fisica = a.cd_pessoa_fisica
and	b.cd_medico  = a.cd_pessoa_fisica
and	d.nr_seq_tumor = b.nr_sequencia
and	d.nr_sequencia = e.NR_SEQ_TRATAMENTO
and 	e.nr_seq_tratamento = nr_seq_tratamento_p
and	e.nr_sequencia = nr_sequencia_p;

end if;

return	ds_medico_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
