CREATE OR REPLACE FUNCTION obter_nome_med_particular ( CD_PESSOA_FISICA_P text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(255);


BEGIN 
 
if (CD_PESSOA_FISICA_P IS NOT NULL AND CD_PESSOA_FISICA_P::text <> '') then 
	select	substr(coalesce(obter_nome_pf(a.cd_medico), ''),1,255) 
	into STRICT	ds_retorno_w 
	from	PESSOA_FISICA a 
	where	a.CD_PESSOA_FISICA	= CD_PESSOA_FISICA_P;
	 
end if;
	 
RETURN ds_retorno_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
