CREATE OR REPLACE FUNCTION obter_nome_mot_devolucao ( nr_devolucao_p bigint, ie_opcao_p text default 'D') RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
ds_motivo_w		varchar(255);
ds_seq_motivo_w		varchar(255);


BEGIN

select	max(obter_valor_dominio(46, x.cd_motivo_devolucao)),
	max(x.cd_motivo_devolucao)
into STRICT	ds_motivo_w,
	ds_seq_motivo_w
from	item_devolucao_material_pac x,
	devolucao_material_pac a
where	x.nr_devolucao = a.nr_devolucao
and	a.nr_devolucao = nr_devolucao_p;

if (ie_opcao_p = 'D') then
	ds_retorno_w := ds_motivo_w;
else
	ds_retorno_w := ds_seq_motivo_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
