CREATE OR REPLACE FUNCTION obter_nome_nao_padrao_mat (cd_material_p material.cd_material%type, nr_atendimento_p atendimento_paciente.nr_atendimento%type) RETURNS varchar AS $body$
DECLARE


ds_material_w 	material.ds_material%type := '';
			

BEGIN

if (cd_material_p IS NOT NULL AND cd_material_p::text <> '' AND nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

	select 	max(substr(ds_medic_nao_padrao,1,255))
	into STRICT 	ds_material_w
	from 	cpoe_material
	where 	cd_material = cd_material_p
	and	nr_atendimento = nr_atendimento_p;
	
end if;

return	ds_material_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
