CREATE OR REPLACE FUNCTION obter_nome_notorio_pac (nm_paciente_p text, cd_pessoa_fisica_p text, nr_prontuario_p text, nr_seq_perfil_pep_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

  DS_REGRA_NOME_w       varchar(255);
  ds_retorno_aux_w      varchar(255);
  nm_paciente_retorno_w varchar(60);

BEGIN
  select max(DS_REGRA_NOME)
    into STRICT DS_REGRA_NOME_w
    from PEP_PERFIL_PACIENTE
   where nr_sequencia = nr_seq_perfil_pep_p;

  if (DS_REGRA_NOME_w IS NOT NULL AND DS_REGRA_NOME_w::text <> '') then
   	 ds_retorno_aux_w	     := substr(replace_macro(DS_REGRA_NOME_w, '@NR_PRONTUARIO', nr_prontuario_p), 1, 255);
	   nm_paciente_retorno_w := substr(replace_macro(ds_retorno_aux_w, '@CD_PESSOA_FISICA', cd_pessoa_fisica_p), 1, 60);
  end if;

  return nm_paciente_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
