CREATE OR REPLACE FUNCTION obter_nome_paciente_lote_opme (nr_lote_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
/* D - nome 
   C - codigo pessoa fisica */
 
					 
nm_pessoa_fisica_w	varchar(255);
cd_pessoa_fisica_w	varchar(10);					
ie_retorno_w		varchar(255);


BEGIN 
 
if (nr_lote_p IS NOT NULL AND nr_lote_p::text <> '') then 
	 
	select MAX(SUBSTR(obter_paciente_prescricao(b.nr_prescricao),1,255)), 
		MAX(SUBSTR(OBTER_DADOS_PRESCRICAO(b.nr_prescricao,'P'),1,255)) 
	into STRICT	nm_pessoa_fisica_w, 
		cd_pessoa_fisica_w 
	from 	lote_producao b 
	where 	b.nr_lote_producao = nr_lote_p;
		 
	IF (ie_opcao_p = 'D') THEN 
		ie_retorno_w	:= nm_pessoa_fisica_w;
	ELSIF (ie_opcao_p = 'C') THEN 
		ie_retorno_w	:= cd_pessoa_fisica_w;
		 
	end if;	
 
end if;
 
 
return ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
