CREATE OR REPLACE FUNCTION obter_nome_paciente_panorama ( cd_pessoa_fisica_p text, cd_perfil_p bigint, nm_paciente_p text ) RETURNS varchar AS $body$
DECLARE

nm_pessoa_w		varchar(255);
ie_tipo_nome_w	varchar(1);

BEGIN

ie_tipo_nome_w := obter_param_usuario(338, 3, cd_perfil_p, obter_usuario_ativo, obter_estabelecimento_ativo, ie_tipo_nome_w);

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	if (ie_tipo_nome_w = 'N') then

		select	substr(coalesce(obter_nome_pf_oculta(cd_pessoa_fisica_p, cd_perfil_p, nm_paciente_p), nm_paciente_p),1,255)
		into STRICT	nm_pessoa_w
		;

	else
		select	substr(coalesce(obter_iniciais_nome(cd_pessoa_fisica_p,null),coalesce(obter_nome_pf_oculta(cd_pessoa_fisica_p, cd_perfil_p, nm_paciente_p), nm_paciente_p)),1,255)
		into STRICT	nm_pessoa_w
		;
	end if;
end if;

return	nm_pessoa_w;

end;
$body$
