CREATE OR REPLACE FUNCTION obter_nome_pessoa_fisica ( CD_PESSOA_FISICA_P text, nm_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(100);


BEGIN
if (nm_pessoa_fisica_p IS NOT NULL AND nm_pessoa_fisica_p::text <> '') then
	ds_retorno_w		:= nm_pessoa_fisica_p;
elsif (CD_PESSOA_FISICA_P IS NOT NULL AND CD_PESSOA_FISICA_P::text <> '') then
	begin
	select SUBSTR(OBTER_NOME_PF(CD_PESSOA_FISICA), 0, 100)
	into STRICT	 ds_retorno_w
	from	 PESSOA_FISICA
	where	 CD_PESSOA_FISICA	= CD_PESSOA_FISICA_P;
	end;
end if;

RETURN ds_retorno_w;
END;
$body$
