CREATE OR REPLACE FUNCTION obter_nome_pessoa_ocupacao ( nr_status_p bigint, nr_seq_unidade_p bigint, cd_pessoa_fisica_p text, cd_perfil_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

nm_pessoa_retorno_w	varchar(255);
nr_sequencia_w		bigint;
nr_atendimento_w	bigint;

BEGIN
if (nr_status_p	in (3,6)) then
	select	max(nr_sequencia)
	into STRICT	nr_sequencia_w
	from	unidade_atend_hist
	where	nr_seq_unidade	= nr_seq_unidade_p
	and	dt_historico = (SELECT	max(dt_historico)
				from	unidade_atend_hist
				where	nr_seq_unidade	= nr_seq_unidade_p);

	select	max(nr_Atendimento)
	into STRICT	nr_atendimento_w
	from	unidade_atend_hist
	where	nr_sequencia= nr_sequencia_w;



	select	max(obter_nome_pf_oculta(cd_pessoa_fisica,cd_perfil_p,nm_usuario_p))
	into STRICT	nm_pessoa_retorno_w
	from	atendimento_paciente
	where	nr_atendimento	= nr_atendimento_w;


else
	nm_pessoa_retorno_w	:= obter_nome_pf_oculta(cd_pessoa_fisica_p,cd_perfil_p,nm_usuario_p);
end if;

return	nm_pessoa_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
