CREATE OR REPLACE FUNCTION obter_nome_pf_oculta ( cd_pessoa_fisica_p text, cd_perfil_p bigint, nm_usuario_p text, nm_pessoa_fisica_p text default null) RETURNS varchar AS $body$
DECLARE


nm_pessoa_w				varchar(255);
nr_seq_person_name_w	person_name.nr_sequencia%type;


BEGIN
select	max(nm_oculto),
		max(nr_seq_person_name)
into STRICT	nm_pessoa_w,
		nr_seq_person_name_w
from	pessoa_fisica_oculta
where	cd_pessoa_fisica = cd_pessoa_fisica_p
and (cd_perfil = cd_perfil_p
		or	coalesce(cd_perfil::text, '') = '')
order by
	coalesce(cd_perfil,0);

if (nr_seq_person_name_w IS NOT NULL AND nr_seq_person_name_w::text <> '') then
	begin
	nm_pessoa_w := substr(pkg_name_utils.get_person_name(nr_seq_person_name_w,null,'full','social,main'),0,255);
	end;
elsif (coalesce(nm_pessoa_w::text, '') = '') then
	begin
	if (nm_pessoa_fisica_p IS NOT NULL AND nm_pessoa_fisica_p::text <> '') then
		begin
		nm_pessoa_w := nm_pessoa_fisica_p;
		end;
	else
		begin
		nm_pessoa_w := substr(obter_nome_pf(cd_pessoa_fisica_p), 0, 255);
		end;
	end if;
	end;
end if;
return nm_pessoa_w;
end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
